import React, { useEffect, useState } from 'react';

const API = process.env.REACT_APP_API || 'http://localhost:4001';

export default function App(){
  const [blocks, setBlocks] = useState([]);
  const [pub, setPub] = useState('');
  const [priv, setPriv] = useState('');
  const [payloadKV, setPayloadKV] = useState('{}');
  const [status, setStatus] = useState('');

  useEffect(()=>{ fetchBlocks(); },[]);

  async function fetchBlocks(){
    const r = await fetch(`${API}/blocks`);
    const data = await r.json();
    setBlocks(data);
  }

  async function genKeys(){
    const r = await fetch(`${API}/keys/generate`);
    const j = await r.json();
    setPub(j.publicKey);
    setPriv(j.privateKey);
    setStatus('Chiavi generate (private key mostrata per comoditÃ  demo).');
  }

  async function addBlock(){
    let payload;
    try{ payload = JSON.parse(payloadKV); } catch (e){ setStatus('JSON non valido'); return; }
    setStatus('Submitting block (mining may take time)...');
    const r = await fetch(`${API}/blocks/add`, {
      method:'POST', headers:{'Content-Type':'application/json'},
      body: JSON.stringify({ payload, owner_public_key_pem: pub })
    });
    const j = await r.json();
    if (r.ok){ setStatus(`Block aggiunto idx=${j.idx} hash=${j.hash}`); fetchBlocks(); }
    else setStatus('Errore: '+JSON.stringify(j));
  }

  async function verifyUpTo(idx){
    const r = await fetch(`${API}/blocks/${idx}/verify`);
    const j = await r.json();
    setStatus(JSON.stringify(j));
  }

  async function decrypt(idx){
    if (!priv) { setStatus('Inserire private key o generare chiavi'); return; }
    const r = await fetch(`${API}/blocks/${idx}/decrypt`, {
      method:'POST', headers:{'Content-Type':'application/json'},
      body: JSON.stringify({ private_key_pem: priv })
    });
    const j = await r.json();
    if (r.ok) setStatus('Decrypted payload: '+JSON.stringify(j.payload));
    else setStatus('Decrypt error: '+JSON.stringify(j));
  }

  return (<div style={{padding:20,fontFamily:'Arial'}}>
    <h2>Sensitive Blockchain Demo</h2>
    <div style={{display:'flex',gap:20}}>
      <div style={{flex:1}}>
        <button onClick={genKeys}>Genera coppia RSA (2048)</button>
        <div style={{marginTop:8}}>
          <strong>Public Key (owner):</strong>
          <textarea rows={6} cols={60} value={pub} onChange={e=>setPub(e.target.value)} />
        </div>
        <div style={{marginTop:8}}>
          <strong>Private Key:</strong>
          <textarea rows={6} cols={60} value={priv} onChange={e=>setPriv(e.target.value)} />
        </div>
      </div>
      <div style={{flex:1}}>
        <h4>Nuovo blocco (payload JSON)</h4>
        <textarea rows={10} cols={60} value={payloadKV} onChange={e=>setPayloadKV(e.target.value)} />
        <div>
          <button onClick={addBlock}>Aggiungi blocco (mina)</button>
        </div>
      </div>
    </div>

    <h3>Catena</h3>
    <div>
      {blocks.map(b=>(
        <div key={b.idx} style={{border:'1px solid #ccc',padding:8,margin:6}}>
          <div><strong>idx:</strong> {b.idx} &nbsp; <strong>hash:</strong> <code>{b.hash}</code></div>
          <div><strong>prev:</strong> <code>{String(b.prev_hash)}</code></div>
          <div><strong>owner:</strong> <small>{b.owner_public_key ? b.owner_public_key.substring(0,60)+'...' : ''}</small></div>
          <div style={{marginTop:6}}>
            <button onClick={()=>verifyUpTo(b.idx)}>Verifica fino a questo</button>
            <button onClick={()=>decrypt(b.idx)} style={{marginLeft:8}}>Decripta col private key</button>
          </div>
        </div>
      ))}
    </div>

    <div style={{marginTop:12,color:'#006'}}>
      <strong>Status:</strong> {status}
    </div>
    <div style={{marginTop:20,fontSize:12,color:'#444'}}>
      Nota: questa demo salva la chain su PostgreSQL. Assicurati di impostare DATABASE_URL o PG_CONN nell'.env del server.
    </div>
  </div>);
}
