// App.js
import React, { useEffect, useState } from 'react';
const API = process.env.REACT_APP_API || 'http://localhost:4001';

export default function App() {
  const [pub, setPub] = useState('');
  const [priv, setPriv] = useState('');
  const [displayName, setDisplayName] = useState('');
  const [creatorId, setCreatorId] = useState('');
  const [dataText, setDataText] = useState('nome Mario Rossi\nemail mario@example.com\ndiagnosi Diabete');
  const [blocks, setBlocks] = useState([]);
  const [notifications, setNotifications] = useState([]);
  const [status, setStatus] = useState('');
  const [decryptName, setDecryptName] = useState('');
  const [decryptPriv, setDecryptPriv] = useState('');
  const [decryptedBlocks, setDecryptedBlocks] = useState([]);

  useEffect(() => {
    fetchBlocks();
    fetchCreatorsList();
  }, []);

  async function fetchBlocks() {
    const r = await fetch(`${API}/blocks`);
    if (r.ok) setBlocks(await r.json());
  }

  async function fetchCreatorsList() {
    const r = await fetch(`${API}/creators`);
    if (r.ok) {
      const list = await r.json();
      // if matched by displayName set creatorId
      const me = list.find(c => c.display_name === displayName);
      if (me) setCreatorId(me.creator_id);
    }
  }

  async function genKeys() {
    const r = await fetch(`${API}/keys/generate`);
    const j = await r.json();
    setPub(j.publicKey);
    setPriv(j.privateKey);
    setStatus('Chiavi generate');
  }

  async function registerCreator() {
    if (!displayName || !pub) { setStatus('Inserisci displayName e genera public key'); return; }
    const r = await fetch(`${API}/creators`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ display_name: displayName, public_key_pem: pub })
    });
    const j = await r.json();
    if (r.ok) {
      setCreatorId(j.creator_id);
      setStatus('Creatore registrato');
      fetchCreatorsList();
    } else {
      setStatus('Errore: ' + JSON.stringify(j));
    }
  }

  // Add block: send displayName + private key + data_text
  async function addBlock() {
    if (!displayName || !priv) { setStatus('Devi specificare displayName e private key per firmare il blocco'); return; }
    setStatus('Mining (potrebbe richiedere tempo)...');
    const r = await fetch(`${API}/blocks`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ display_name: displayName, private_key_pem: priv, data_text: dataText })
    });
    const j = await r.json();
    if (r.ok) {
      setStatus('Blocco aggiunto: ' + j.block_hash);
      fetchBlocks();
    } else {
      setStatus('Errore: ' + JSON.stringify(j));
    }
  }

  // Verify block (server recalculates hash and signature)
  async function verifyBlock(block_id) {
    const r = await fetch(`${API}/blocks/${block_id}/verify`, { method: 'POST' });
    const j = await r.json();
    if (r.ok) {
      setStatus('Verifica OK');
      fetchBlocks();
    } else {
      setStatus('Verifica KO: ' + JSON.stringify(j));
    }
  }

  // Poll notifications for current creator
  async function fetchNotifications() {
    if (!creatorId) { setStatus('Seleziona/Registra un creatore prima'); return; }
    const r = await fetch(`${API}/notifications?creator_id=${creatorId}`);
    if (r.ok) {
      setNotifications(await r.json());
    } else {
      setStatus('Errore notifiche');
    }
  }

  async function markSeen(id) {
    await fetch(`${API}/notifications/${id}/mark-seen`, { method: 'POST' });
    fetchNotifications();
  }

  // Decrypt blocks for a user (sends display_name + private key to server)
  async function decryptForUser() {
    if (!decryptName || !decryptPriv) { setStatus('Inserisci nome utente e private key per decriptare'); return; }
    const r = await fetch(`${API}/decrypt`, {
      method: 'POST', headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ display_name: decryptName, private_key_pem: decryptPriv })
    });
    const j = await r.json();
    if (r.ok) {
      setDecryptedBlocks(j);
      setStatus('Decriptazione completata');
    } else {
      setStatus('Errore decriptazione: ' + JSON.stringify(j));
    }
  }

  return (
    <div style={{ fontFamily: 'sans-serif', maxWidth: 900, margin: '0 auto' }}>
      <h2>🔐 Sensitive Blockchain (demo)</h2>

      <section style={{ border: '1px solid #ddd', padding: 10, marginBottom: 10 }}>
        <h3>Chiavi</h3>
        <button onClick={genKeys}>Genera coppia RSA (2048)</button>
        <div style={{ display: 'flex', gap: 10, marginTop: 8 }}>
          <textarea rows={4} style={{ flex: 1 }} value={pub} onChange={e => setPub(e.target.value)} placeholder="Public PEM" />
          <textarea rows={4} style={{ flex: 1 }} value={priv} onChange={e => setPriv(e.target.value)} placeholder="Private PEM" />
        </div>
      </section>

      <section style={{ border: '1px solid #ddd', padding: 10, marginBottom: 10 }}>
        <h3>Registrazione creatore</h3>
        <input placeholder="Nome utente unico" value={displayName} onChange={e => setDisplayName(e.target.value)} />
        <button onClick={registerCreator} style={{ marginLeft: 8 }}>Registra creatore (usa public key)</button>
        <div style={{ marginTop: 8 }}>CreatorId: {creatorId || '(non registrato)'}</div>
      </section>

      <section style={{ border: '1px solid #ddd', padding: 10, marginBottom: 10 }}>
        <h3>Aggiungi nuovo blocco</h3>
        <div>
          <small>Formato: <code>chiave valore</code> per riga (es. <code>nome Mario Rossi</code>)</small>
        </div>
        <textarea rows={6} style={{ width: '100%', marginTop: 8 }} value={dataText} onChange={e => setDataText(e.target.value)} />
        <div style={{ marginTop: 8 }}>
          <button onClick={addBlock}>⛏️ Mina e aggiungi blocco (firma con private key)</button>
        </div>
      </section>

      <section style={{ border: '1px solid #ddd', padding: 10, marginBottom: 10 }}>
        <h3>Catena (intestazioni pubbliche)</h3>
        <button onClick={fetchBlocks}>Aggiorna lista</button>
        {blocks.map(b => (
          <div key={b.block_id} style={{ border: '1px solid #ccc', padding: 8, marginTop: 8 }}>
            <div><b>Hash:</b> {b.block_hash}</div>
            <div><b>Prev:</b> {b.previous_hash}</div>
            <div><b>Creator:</b> {b.display_name || b.creator_id}</div>
            <div><b>Created:</b> {b.created_at}</div>
            <div><b>Nonce:</b> {b.nonce}</div>
            <div><b>Verified:</b> {b.verified ? '✅' : '❌'}</div>
            <div style={{ marginTop: 6 }}>
              <button onClick={() => verifyBlock(b.block_id)}>Verifica blocco</button>
            </div>
          </div>
        ))}
      </section>

      <section style={{ border: '1px solid #ddd', padding: 10, marginBottom: 10 }}>
        <h3>Notifiche (distribuzione)</h3>
        <div>
          <button onClick={fetchNotifications}>Controlla notifiche per il mio creator</button>
        </div>
        {notifications.map(n => (
          <div key={n.id} style={{ border: '1px dashed #aaa', padding: 8, marginTop: 8 }}>
            <div><b>Notified at:</b> {n.notified_at}</div>
            <div><b>Block:</b> {n.block_hash}</div>
            <div><b>From:</b> {n.creator_name}</div>
            <div style={{ marginTop: 6 }}>
              <button onClick={() => { markSeen(n.id); }}>Segna come letto</button>
            </div>
          </div>
        ))}
      </section>

      <section style={{ border: '1px solid #ddd', padding: 10, marginBottom: 10 }}>
        <h3>Decripta blocchi per utente</h3>
        <div style={{ marginBottom: 6 }}>
          <input placeholder="Nome utente (display_name)" value={decryptName} onChange={e => setDecryptName(e.target.value)} style={{ width: 300 }} />
        </div>
        <div style={{ marginBottom: 6 }}>
          <textarea rows={4} style={{ width: 600 }} placeholder="Private key PEM" value={decryptPriv} onChange={e => setDecryptPriv(e.target.value)} />
        </div>
        <button onClick={decryptForUser}>🔓 Decripta blocchi di questo utente</button>

        <div style={{ marginTop: 10 }}>
          <h4>Risultati decriptazione</h4>
          <pre style={{ maxHeight: 300, overflow: 'auto', background: '#fafafa', padding: 8 }}>{JSON.stringify(decryptedBlocks, null, 2)}</pre>
        </div>
      </section>

      <div style={{ marginTop: 12, padding: 8, background: '#f6f6f6' }}>
        <b>Status:</b> {status}
      </div>
    </div>
  );
}
