import React, { useEffect, useState } from 'react';

const API = process.env.REACT_APP_API || 'http://localhost:4001';

export default function App() {
  const [displayName, setDisplayName] = useState('');
  const [pub, setPub] = useState('');
  const [priv, setPriv] = useState('');
  const [dataText, setDataText] = useState('nome valore\nindirizzo via roma');
  const [blocks, setBlocks] = useState([]);
  const [decrypted, setDecrypted] = useState([]);
  const [status, setStatus] = useState('');

  useEffect(() => { fetchBlocks(); }, []);

  async function fetchBlocks() {
    const r = await fetch(`${API}/blocks`);
    setBlocks(await r.json());
  }

  async function genKeys() {
    const r = await fetch(`${API}/keys/generate`);
    const j = await r.json();
    setPub(j.publicKey);
    setPriv(j.privateKey);
    setStatus('🔑 Chiavi generate');
  }

  async function registerCreator() {
    const r = await fetch(`${API}/creators`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ display_name: displayName, public_key_pem: pub })
    });
    const j = await r.json();
    if (r.ok) setStatus('✅ Creatore registrato');
    else setStatus('Errore: ' + JSON.stringify(j));
  }

  async function addBlock() {
    setStatus('⛏️ Mining...');
    const r = await fetch(`${API}/blocks`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ display_name: displayName, private_key_pem: priv, data_text: dataText })
    });
    const j = await r.json();
    if (r.ok) {
      setStatus('✅ Blocco aggiunto: ' + j.block_hash);
      fetchBlocks();
    } else setStatus('Errore: ' + JSON.stringify(j));
  }

  async function decryptBlocks() {
    const r = await fetch(`${API}/decrypt`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ display_name: displayName, private_key_pem: priv })
    });
    const j = await r.json();
    setDecrypted(j);
  }

  return (
    <div style={{ maxWidth: 800, margin: '0 auto', fontFamily: 'sans-serif' }}>
      <h2>🔐 Sensitive Blockchain</h2>

      <section>
        <button onClick={genKeys}>Genera chiavi RSA</button>
        <textarea rows={4} value={pub} onChange={e => setPub(e.target.value)} placeholder="Public PEM" />
        <textarea rows={4} value={priv} onChange={e => setPriv(e.target.value)} placeholder="Private PEM" />
      </section>

      <section>
        <input placeholder="Nome utente unico" value={displayName} onChange={e => setDisplayName(e.target.value)} />
        <button onClick={registerCreator}>Registra creatore</button>
      </section>

      <section>
        <h4>Nuovo blocco</h4>
        <textarea rows={6} value={dataText} onChange={e => setDataText(e.target.value)} />
        <button onClick={addBlock}>⛏️ Aggiungi blocco</button>
      </section>

      <section>
        <h3>Catena</h3>
        {blocks.map(b => (
          <div key={b.block_id} style={{ border: '1px solid #ccc', margin: '5px 0', padding: 5 }}>
            <div><b>Hash:</b> {b.block_hash}</div>
            <div><b>Prev:</b> {b.previous_hash}</div>
            <div><b>Nonce:</b> {b.nonce}</div>
          </div>
        ))}
      </section>

      <section>
        <h4>🔓 Decripta dati utente</h4>
        <button onClick={decryptBlocks}>Decripta</button>
        <pre>{JSON.stringify(decrypted, null, 2)}</pre>
      </section>

      <div><b>Status:</b> {status}</div>
    </div>
  );
}
