import React, { useEffect, useState } from 'react';

const API = process.env.REACT_APP_API || 'http://localhost:4001';

export default function App() {
  const [blocks, setBlocks] = useState([]);
  const [creators, setCreators] = useState([]);
  const [pub, setPub] = useState('');
  const [priv, setPriv] = useState('');
  const [displayName, setDisplayName] = useState('');
  const [creatorId, setCreatorId] = useState('');
  const [payloadText, setPayloadText] = useState('{"hello":"world"}');
  const [status, setStatus] = useState('');

  useEffect(() => {
    fetchBlocks();
    fetchCreators();
  }, []);

  async function fetchBlocks() {
    const r = await fetch(`${API}/blocks`);
    setBlocks(await r.json());
  }

  async function fetchCreators() {
    const r = await fetch(`${API}/creators`);
    setCreators(await r.json());
  }

  async function genKeys() {
    const r = await fetch(`${API}/keys/generate`);
    const j = await r.json();
    setPub(j.publicKey);
    setPriv(j.privateKey);
    setStatus('Chiavi generate ✅');
  }

  async function registerCreator() {
    if (!pub) return setStatus('Genera prima la chiave pubblica');
    const r = await fetch(`${API}/creators`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ display_name: displayName, pgp_public_key: pub })
    });
    const j = await r.json();
    if (r.ok) {
      setCreatorId(j.creator_id);
      setStatus('Creatore registrato ✅');
      fetchCreators();
    } else setStatus('Errore: ' + JSON.stringify(j));
  }

  async function addBlock() {
    let payload;
    try {
      payload = JSON.parse(payloadText);
    } catch {
      setStatus('Payload non valido');
      return;
    }
    setStatus('Mining in corso...');
    const r = await fetch(`${API}/blocks`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ creator_id: creatorId, payload, public_key_pem: pub, private_key_pem: priv })
    });
    const j = await r.json();
    if (r.ok) {
      setStatus(`Blocco aggiunto: ${j.block_hash}`);
      fetchBlocks();
    } else {
      setStatus('Errore: ' + JSON.stringify(j));
    }
  }

  return (
    <div className="app">
      <h2>Blockchain sensibile - nuova versione</h2>

      <div className="panel">
        <button onClick={genKeys}>🔑 Genera coppia RSA</button>
        <textarea rows={4} value={pub} onChange={(e) => setPub(e.target.value)} placeholder="Public PEM" />
        <textarea rows={4} value={priv} onChange={(e) => setPriv(e.target.value)} placeholder="Private PEM" />
      </div>

      <div className="panel">
        <h4>Registrazione creatore</h4>
        <input value={displayName} onChange={(e) => setDisplayName(e.target.value)} placeholder="Display Name" />
        <button onClick={registerCreator}>➕ Registra</button>
      </div>

      <div className="panel">
        <h4>Nuovo blocco</h4>
        <textarea rows={6} value={payloadText} onChange={(e) => setPayloadText(e.target.value)} />
        <button onClick={addBlock}>⛏️ Aggiungi blocco</button>
      </div>

      <h3>Catena</h3>
      {blocks.map((b) => (
        <div key={b.block_id} className="block">
          <div><strong>Hash:</strong> {b.block_hash}</div>
          <div><strong>Prev:</strong> {b.previous_hash}</div>
          <div><strong>Nonce:</strong> {b.nonce}</div>
          <div><small>{b.created_at}</small></div>
        </div>
      ))}

      <div className="status">{status}</div>
    </div>
  );
}
